SourceCode.Declaration:
/// <summary>
/// Attribute to mark method as message handler.
/// </summary>
public class WHSRFMessageAuxAttribute extends SysAttribute
{
    private str name;

}

Method.Source:
/// <summary>
/// Standard constructor.
/// </summary>
/// <param name = "_name">Message name.</param>
void new(str _name)
{
    super();

    name = _name;
}

/// <summary>
/// Standard parm method.
/// </summary>
/// <param name = "_name">Message name.</param>
/// <returns>Return value.</returns>
public str parmName(str _name = name)
{
    name = _name;

    return name;
}
