void new(int _step, str _title = '')
{
    super();

    step = _step;
    title = _title;
}

/// <summary>
/// Standard parm method.
/// </summary>
/// <param name = "_step">Value to set.</param>
/// <returns>Return value.</returns>
public int parmStep(int _step = step)
{
    step = _step;

    return step;
}

public str parmTitle(str _title = title)
